    "targets = {\n",
    "    '실리콘투': '257720', '삼양식품': '003230', '클래시스': '214150',\n",
    "    '브이티': '018290', '에이피알': '278470', '파마리서치': '214450',\n",
    "    '아이패밀리에스씨': '114840'\n",
    "}\n",
    "\n",
    "today = \"20251205\"\n",
    "years = ['2022', '2023', '2024']\n",
    "cap_df = stock.get_market_cap_by_ticker(today)\n",
    "\n",
    "results = []\n",
    "\n",
    "for name, code in targets.items():\n",
    "    try:\n",
    "        # 재무 데이터\n",
    "        financials = {}\n",
    "        for year in years:\n",
    "            fs = dart.finstate(corp=code, bsns_year=year)\n",
    "            if fs is None: continue\n",
    "            \n",
    "            def get_val(acct):\n",
    "                row = fs[(fs['fs_div'] == 'CFS') & (fs['account_nm'].isin(acct))]\n",
    "                if row.empty: return 0\n",
    "                return int(row['thstrm_amount'].iloc[0].replace(',', ''))\n",
    "\n",
    "            sales = get_val(['매출액', '수익(매출액)'])\n",
    "            op = get_val(['영업이익', '영업이익(손실)'])\n",
    "            ni = get_val(['당기순이익', '당기순이익(손실)'])\n",
    "            \n",
    "            financials[year] = {'매출액': sales, '영업이익': op, '당기순이익': ni}\n",
    "            time.sleep(0.1)\n",
    "        \n",
    "        if len(financials) < 3: \n",
    "            continue\n",
    "\n",
    "        # 영업이익률\n",
    "        opm_22 = (financials['2022']['영업이익'] / financials['2022']['매출액']) * 100\n",
    "        opm_23 = (financials['2023']['영업이익'] / financials['2023']['매출액']) * 100\n",
    "        opm_24 = (financials['2024']['영업이익'] / financials['2024']['매출액']) * 100\n",
    "        \n",
    "        opm_score = \"\"\n",
    "        if opm_24 > opm_23 > opm_22:\n",
    "            opm_score = \"지속 상승\"\n",
    "        elif opm_24 > opm_23:\n",
    "            opm_score = \"개선 중\"\n",
    "        elif opm_24 >= 15.0:\n",
    "            opm_score = \"고마진 달성 중\"\n",
    "        else:\n",
    "            opm_score = \"영업이익률 낮음\"\n",
    "\n",
    "        # PEG\n",
    "        if code in cap_df.index:\n",
    "            market_cap = cap_df.loc[code, '시가총액']\n",
    "        else:\n",
    "            market_cap = stock.get_market_cap_by_ticker(today, market=\"KOSDAQ\").loc[code, '시가총액']\n",
    "\n",
    "        net_income_24 = financials['2024']['당기순이익']\n",
    "        net_income_22 = financials['2022']['당기순이익']\n",
    "        \n",
    "        peg = 999\n",
    "        growth_rate_pct = 0\n",
    "        per = 0\n",
    "\n",
    "        if net_income_24 > 0 and net_income_22 > 0:\n",
    "            \n",
    "            per = market_cap / net_income_24\n",
    "            growth_rate = ((net_income_24 / net_income_22) ** (1/2)) - 1\n",
    "            growth_rate_pct = growth_rate * 100 # 연평균 성장률\n",
    "            \n",
    "            if growth_rate_pct > 0:\n",
    "                peg = per / growth_rate_pct\n",
    "\n",
    "        # 결과\n",
    "        results.append({\n",
    "            '종목명': name,\n",
    "            'OPM추세': f\"{opm_22:.1f}% -> {opm_23:.1f}% -> {opm_24:.1f}%\",\n",
    "            '판정': opm_score,\n",
    "            'PER': round(per, 2),\n",
    "            '연평균성장률': round(growth_rate_pct, 1),\n",
    "            'PEG': round(peg, 2)\n",
    "        })\n",
    "        \n",
    "        print(f\"{name} 분석 완료\")\n",
    "\n",
    "    except Exception as e:\n",
    "        print(f\"Error {name}: {e}\")\n",
    "        continue\n",
    "\n",
    "if len(results) > 0:\n",
    "    df_final = pd.DataFrame(results)\n",
